package C_few_Things_specific_to_Java;

public class A_Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//manual Casting
		double number = 55.0;
		int number2 = (int) number;
		System.out.println(number2);
		
		
		System.out.println();
	
	
		
		//auto casting
		int no = 8;
		double no2 = no;
		System.out.println(no2);
		
		
		
	}

}
